import socket
import struct
import utils

server_name = socket.getfqdn()
server_port = 8080
s = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
s.connect((server_name, server_port))
print('Connected to server ', server_name)

exprs = [
    b'1-3+4',
    b'12345+12345-54321',
    b'98765',
    b'',
    b''
]

utils.write_expressions(s, exprs)
ans = utils.read_expressions(s)

for i in range(len(exprs)):
    print(exprs[i], 'is', ans[i], 'and expected answer is', eval(exprs[i]))

s.close()
